var localStorage = window.localStorage,
    themeSwitcher = document.querySelector('.day-night-switcher'),
    tweets = document.querySelectorAll(".twitter-tweet");

if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches && localStorage.getItem('MrFirthyTheme') != 'light') {
    document.body.classList.add('night');
    localStorage.setItem("MrFirthyTheme", "night");
    for(var i=0;i<tweets.length;++i)tweets[i].setAttribute("data-theme","dark");
}

themeSwitcher.onclick = function() {
	document.body.classList.toggle('night');
    var status = document.body.classList.contains("night") ? "night" : "light";
    localStorage.setItem("MrFirthyTheme", status);
}
